import React, { Component } from "react";

class NewQuestion extends Component {
  state = {
    optionOneInput: "",
    optionTwoInput: ""
  };

  handleChange(e) {
    const { text } = e.target.value;
    console.log(text);
    this.setState(
      () => ({
        optionOneInput: text
      }),
      console.log(this.state.optionOneInput)
    );
  }

  handleSubmit(e) {}

  render() {
    return (
      <div>
        <h2>New Question</h2>
        <div className="single-question-view question-border">
          <h3>Would you rather...</h3>
          <form onSubmit={this.handleSubmit}>
            <input
              value={this.state.optionOneInput}
              name="option1"
              type="text"
              placeholder="Answer 1..."
              onChange={this.handleChange.bind(this)}
            />
            <input
              value={this.state.optionTwoInput}
              name="option2"
              type="text"
              placeholder="Answer 2..."
              onChange={this.handleChange.bind(this)}
            />
          </form>
          <div className="center-btn">
            <button type="submit" className="btn btn-primary add-ques-btn">
              SUBMIT
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default NewQuestion;
